
CREATE TABLE traffic_violations (
    id INT AUTO_INCREMENT PRIMARY KEY,
    PlateNumber VARCHAR(20),
    DriverName VARCHAR(100),
    Description TEXT,
    Belts VARCHAR(10),
    Personal_Injury VARCHAR(10),
    Property_Damage VARCHAR(10),
    Commercial_License VARCHAR(10),
    Commercial_Vehicle VARCHAR(10),
    State VARCHAR(10),
    VehicleType VARCHAR(50),
    Year INT,
    Make VARCHAR(50),
    Model VARCHAR(50),
    Color VARCHAR(20),
    Charge VARCHAR(50),
    PenaltyAmount DECIMAL(10,2),
    Contributed_To_Accident VARCHAR(10),
    Race VARCHAR(50),
    Gender VARCHAR(10),
    Driver_City VARCHAR(100),
    Driver_State VARCHAR(10),
    DL_State VARCHAR(10),
    Arrest_Type VARCHAR(50),
    Violation_Type VARCHAR(50)
);

